// Render dynamic carousel for individual page NEWS | ?user="firstname-lastname"
var newsJsonData = newsData.data;
var newsPageData = {
    data: []
};
var newsItemCount;

for (var i = 0; i < newsJsonData.length; i++) {
    var userIndex1 = newsJsonData[i].id1;
    var userIndex2 = newsJsonData[i].id2;
    if (window.location.href.indexOf(userIndex1) > -1 || window.location.href.indexOf(userIndex2) > -1 ) {
        newsPageData.data.push(newsJsonData[i]); 
        newsItemCount = newsPageData.data.length;
        console.log(newsPageData.data.length);
    } 
}

var count1;
var count2;
var count3;

if (newsItemCount < 1 || newsItemCount == undefined || newsItemCount == null) {

    $('#page--news').hide();

} else if (newsItemCount == 1) {

    newsPageData.data.forEach(news => $('.owl-carousel.profile--fellows').append("<div class='grid-col-12 tablet:grid-col-3 outer-div'>" + "<div class='usa-card__container'>" + "<a href='" + news.url + "' />" + "<div class='usa-card__media usa-card__media--inset' >" + "<img src='" + news.img + "'alt='" + news.title + "' />" + "</div>" + "<div class='usa-card__body'>" + "<h4>" + news.title + "</h4>" + "<p>" + news.description + "</p>" + "</div>" + "</a>" + "</div>" + "</div>")); 

    $(".profile--fellows").css("display", "flex");
    $(".profile--fellows > div.outer-div").css("min-width", "334px");

} else if (newsItemCount < 5 && newsItemCount > 1) {

    newsPageData.data.forEach(news => $('.owl-carousel.profile--fellows').append("<div class='usa-card__container item'>" + "<a href='" + news.url + "' />" + "<div class='usa-card__media usa-card__media--inset' >" + "<img src='" + news.img + "'alt='" + news.title + "' />" + "</div>" + "<div class='usa-card__body'>" + "<h4>" + news.title + "</h4>" + "<p>" + news.description + "</p>" + "</div>" + "</a>" + "</div>")); 

    count1 = 1;
    count2 = 4;
    count3 = 4;
    shouldLoop = false

    $('.owl-carousel.profile--fellows').owlCarousel({
        loop: true,
        margin: 10,
        nav: true,
        autoplay: shouldLoop,
        autoplayTimeout: 3000,
        responsive:{
            0:{
                items: count1
            },
            600:{
                items: count2
            },
            1000:{
                items: count3
            }
        }
    });

    $(".owl-stage div.cloned.active").css("visibility", "hidden");

} else {

    newsPageData.data.forEach(news => $('.owl-carousel.profile--fellows').append("<div class='usa-card__container item'>" + "<a href='" + news.url + "' />" + "<div class='usa-card__media usa-card__media--inset' >" + "<img src='" + news.img + "'alt='" + news.title + "' />" + "</div>" + "<div class='usa-card__body'>" + "<h4>" + news.title + "</h4>" + "<p>" + news.description + "</p>" + "</div>" + "</a>" + "</div>")); 

    count1 = 1;
    count2 = 2;
    count3 = 4;
    shouldLoop = true

    $('.owl-carousel.profile--fellows').owlCarousel({
        loop: true,
        margin: 10,
        nav: true,
        autoplay: shouldLoop,
        autoplayTimeout: 3000,
        responsive:{
            0:{
                items: count1
            },
            600:{
                items: count2
            },
            1000:{
                items: count3
            }
        }
    });
}

$(".page-news--grid div:empty").remove();
$(".page-news--grid h4:empty").remove();
$(".page-news--grid p:empty").remove();

$(".page-news--row div:empty").remove();
$(".page-news--row h4:empty").remove();
$(".page-news--row p:empty").remove();



